import React from "react";
import styled from "styled-components";
import "./home.css";

function Home() {
  return (
    <div className="home">
      <input>
        <h2>Search for events through either Zip Code or City, State</h2>
        <input
          type="text"
          placeholder="'10002' or 'Brooklyn, New York'"
          onChange={(e) => {
            console.log(e.target.value);
          }}
        ></input>
      </input>
      <div className="filter">
        <label>Filter:</label>
        <label for="age">Select Age Requirement(s)</label>
        <select name="age">
          <option value="all-ages">All-Ages</option>
          <option value="teens">Teens</option>
          <option value="21up">21+</option>
        </select>
        <label for="start-date-time">Select Event Start Date</label>
        <input type="datetime-local"></input>
        <label for="end-date-time">Select Event End Date</label>
        <input type="datetime-local"></input>
      </div>
      <div className="results">Display Results Here</div>
    </div>
  );
}

const SearchBar = styled.input`
  width: 75%;
  height: 40px;
  box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.75);
  border: none;
  border-radius: 40px;
  background-color: #bebd7f;
  font-size: 20px;
  background-color: #f75e25;
  box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.5);
  text-align: center;
`;
const Search = styled.div``;
const Filter = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: flex-start;
`;
const Display = styled.div``;
export default Home;
