import React, {Component} from "react";
import styled from "styled-components";
// import eventDB from "./data/events.json";

class Events extends React.Component{
  state = {
    isLoading: true,
    users: [],
    error: null
};
getFetchUsers() {
    this.setState({
        loading: true
    }, () => {
        fetch("http://localhost:8000/events").then(res => res.json()).then(result => this.setState({
            loading: false,
            users: result
        })).catch(console.log);
    });
}
componentDidMount() {
    this.getFetchUsers();
}
render() {
    const {
        events,
        error
    } = this.state;
    return (
      <React.Fragment>
      <h1>All User</h1>
      {
        events.map(event => {
          const {
            zipcode,
            eventName,
            city,
            state,
          } = event;
          return (
            <div key={zipcode}>
              <p>Event Name: {eventName}</p>
              <p>City: {city}</p>
              <p>State: {state}</p>
              <p>Zipcode: {zipcode}</p>
            </div>
          )
        })
      }
      </React.Fragment>
      );
    }

}

const Event = styled.div`
  width: 15%;
  height: 300px;
`;

export default Event;
