import React, {Component} from "react";
import styled from "styled-components";
// import eventDB from "./data/events.json";

class Events extends React.Component{
  state = {
    isLoading: true,
    users: [],
    error: null
};
getFetchUsers() {
    this.setState({
        loading: true
    }, () => {
        fetch("http://localhost:8000/events").then(res => res.json()).then(result => this.setState({
            loading: false,
            users: result
        })).catch(console.log);
    });
}
componentDidMount() {
    this.getFetchUsers();
}
render() {
    const {
        users,
        error
    } = this.state;
    return (
      <React.Fragment>
      <h1>All User</h1>
      {
        users.map(user => {
          const {
            adderss,
            name,
            email
        } = user;
                    return (
                    <div key={name}>
                        <p>Name: {name}</p>
                        <p>Email: {email}</p>
                        <p>Address: {adderss}</p>
                        <hr />
                    </div>
                    );
                })
            } < /React.Fragment> );
    }

}

const Event = styled.div`
  width: 15%;
  height: 300px;
`;

export default Event;
